/* https://github.com/tyleruebele/details-shim */
function details_shim(){var a=document.getElementsByTagName('summary');for(f in a){if(a[f].parentNode){var details=a[f].parentNode;if('boolean'!=typeof details.open){var s=details.outerHTML;s=s.substring(0,s.indexOf('>'));s=(-1!=s.indexOf('open')&&-1==s.indexOf('open=""'))?'open':'closed';details.setAttribute('data-open',s);details.className+=' '+s;a[f].onclick=function(){var s=this.parentNode.getAttribute('data-open');s=s=='open'?'closed':'open';this.parentNode.setAttribute('data-open',s);this.parentNode.className=this.parentNode.className.replace(/\bopen\b|\bclosed\b/g,' ')+' '+s;};var sibs=details.childNodes;for(ff in sibs){if(sibs[ff].nodeType==3&&/[^\s]/.test(sibs[ff].data)){var span=document.createElement('span');var text=sibs[ff];details.insertBefore(span,text);details.removeChild(text);span.appendChild(text);}}}}}}
window.addEventListener?window.addEventListener('load',details_shim,false):window.attachEvent&&window.attachEvent('onload',details_shim);
